-- Create admin_levels enum
CREATE TYPE admin_level AS ENUM ('ADMIN_1', 'ADMIN_2', 'ADMIN_3', 'ADMIN_4');

-- Create sanction_type enum
CREATE TYPE sanction_type AS ENUM ('MESSAGE_BAN', 'COMMENT_BAN', 'FULL_BAN');

-- Create sanction_status enum
CREATE TYPE sanction_status AS ENUM ('ACTIVE', 'REVOKED', 'EXPIRED');

-- Create action_type enum for audit log
CREATE TYPE action_type AS ENUM ('sanction_created', 'sanction_revoked', 'admin_promoted', 'admin_demoted', 'admin_removed');

-- Create profiles table (extends auth.users)
CREATE TABLE IF NOT EXISTS profiles (
  id UUID PRIMARY KEY REFERENCES auth.users(id) ON DELETE CASCADE,
  name TEXT NOT NULL,
  email TEXT,
  role TEXT NOT NULL DEFAULT 'trainer',
  avatar TEXT,
  cover_image TEXT,
  bio TEXT,
  location TEXT,
  admin_level admin_level,
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- Create sanctions table
CREATE TABLE IF NOT EXISTS sanctions (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  user_id UUID NOT NULL REFERENCES profiles(id) ON DELETE CASCADE,
  type sanction_type NOT NULL,
  reason TEXT NOT NULL,
  created_by_admin UUID NOT NULL REFERENCES profiles(id),
  created_at TIMESTAMPTZ DEFAULT NOW(),
  is_permanent BOOLEAN DEFAULT FALSE,
  ends_at TIMESTAMPTZ,
  status sanction_status DEFAULT 'ACTIVE',
  revoked_at TIMESTAMPTZ,
  revoked_by_admin UUID REFERENCES profiles(id)
);

-- Create admin_chat_messages table
CREATE TABLE IF NOT EXISTS admin_chat_messages (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  admin_id UUID NOT NULL REFERENCES profiles(id) ON DELETE CASCADE,
  content TEXT NOT NULL,
  image_url TEXT,
  created_at TIMESTAMPTZ DEFAULT NOW()
);

-- Create audit_log table
CREATE TABLE IF NOT EXISTS audit_log (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  actor_admin_id UUID NOT NULL REFERENCES profiles(id),
  target_user_id UUID REFERENCES profiles(id),
  action_type action_type NOT NULL,
  details JSONB,
  timestamp TIMESTAMPTZ DEFAULT NOW()
);

-- Create indexes for performance
CREATE INDEX idx_profiles_admin_level ON profiles(admin_level) WHERE admin_level IS NOT NULL;
CREATE INDEX idx_sanctions_user_id ON sanctions(user_id);
CREATE INDEX idx_sanctions_status ON sanctions(status);
CREATE INDEX idx_sanctions_type ON sanctions(type);
CREATE INDEX idx_admin_chat_created_at ON admin_chat_messages(created_at DESC);
CREATE INDEX idx_audit_log_timestamp ON audit_log(timestamp DESC);
CREATE INDEX idx_audit_log_actor ON audit_log(actor_admin_id);
CREATE INDEX idx_audit_log_target ON audit_log(target_user_id);

-- Enable RLS on all tables
ALTER TABLE profiles ENABLE ROW LEVEL SECURITY;
ALTER TABLE sanctions ENABLE ROW LEVEL SECURITY;
ALTER TABLE admin_chat_messages ENABLE ROW LEVEL SECURITY;
ALTER TABLE audit_log ENABLE ROW LEVEL SECURITY;

-- RLS Policies for profiles
CREATE POLICY "Public profiles are viewable by everyone"
  ON profiles FOR SELECT
  USING (true);

CREATE POLICY "Users can update own profile"
  ON profiles FOR UPDATE
  USING (auth.uid() = id);

CREATE POLICY "Users can insert own profile"
  ON profiles FOR INSERT
  WITH CHECK (auth.uid() = id);

-- RLS Policies for sanctions
CREATE POLICY "Admins can view all sanctions"
  ON sanctions FOR SELECT
  USING (
    EXISTS (
      SELECT 1 FROM profiles
      WHERE profiles.id = auth.uid()
      AND profiles.admin_level IS NOT NULL
    )
  );

CREATE POLICY "Admin 2-4 can create sanctions"
  ON sanctions FOR INSERT
  WITH CHECK (
    EXISTS (
      SELECT 1 FROM profiles
      WHERE profiles.id = auth.uid()
      AND profiles.admin_level IN ('ADMIN_2', 'ADMIN_3', 'ADMIN_4')
    )
  );

CREATE POLICY "Admin 2-4 can update sanctions"
  ON sanctions FOR UPDATE
  USING (
    EXISTS (
      SELECT 1 FROM profiles
      WHERE profiles.id = auth.uid()
      AND profiles.admin_level IN ('ADMIN_2', 'ADMIN_3', 'ADMIN_4')
    )
  );

-- RLS Policies for admin_chat_messages
CREATE POLICY "Admins can view admin chat"
  ON admin_chat_messages FOR SELECT
  USING (
    EXISTS (
      SELECT 1 FROM profiles
      WHERE profiles.id = auth.uid()
      AND profiles.admin_level IS NOT NULL
    )
  );

CREATE POLICY "Admins can send messages"
  ON admin_chat_messages FOR INSERT
  WITH CHECK (
    EXISTS (
      SELECT 1 FROM profiles
      WHERE profiles.id = auth.uid()
      AND profiles.admin_level IS NOT NULL
    )
    AND admin_id = auth.uid()
  );

-- RLS Policies for audit_log
CREATE POLICY "Admins can view audit log"
  ON audit_log FOR SELECT
  USING (
    EXISTS (
      SELECT 1 FROM profiles
      WHERE profiles.id = auth.uid()
      AND profiles.admin_level IS NOT NULL
    )
  );

CREATE POLICY "System can insert audit log"
  ON audit_log FOR INSERT
  WITH CHECK (true);

-- Function to check active sanctions
CREATE OR REPLACE FUNCTION get_active_sanctions(p_user_id UUID)
RETURNS TABLE (
  id UUID,
  type sanction_type,
  reason TEXT,
  is_permanent BOOLEAN,
  ends_at TIMESTAMPTZ,
  created_by_admin_name TEXT
) AS $$
BEGIN
  RETURN QUERY
  SELECT 
    s.id,
    s.type,
    s.reason,
    s.is_permanent,
    s.ends_at,
    p.name as created_by_admin_name
  FROM sanctions s
  JOIN profiles p ON s.created_by_admin = p.id
  WHERE s.user_id = p_user_id
    AND s.status = 'ACTIVE'
    AND (s.is_permanent = true OR s.ends_at > NOW());
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Function to automatically expire sanctions
CREATE OR REPLACE FUNCTION expire_sanctions()
RETURNS void AS $$
BEGIN
  UPDATE sanctions
  SET status = 'EXPIRED'
  WHERE status = 'ACTIVE'
    AND is_permanent = false
    AND ends_at <= NOW();
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Trigger to update updated_at on profiles
CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
  NEW.updated_at = NOW();
  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

CREATE TRIGGER update_profiles_updated_at
  BEFORE UPDATE ON profiles
  FOR EACH ROW
  EXECUTE FUNCTION update_updated_at_column();

-- Create a function to automatically promote the owner account to Admin 4
CREATE OR REPLACE FUNCTION auto_promote_owner()
RETURNS TRIGGER AS $$
BEGIN
  -- Check if the email matches the owner email
  IF NEW.email = 'tomsc.rp@gmail.com' THEN
    -- Update the profile to set role as ADMIN and admin_level as ADMIN_4
    UPDATE profiles 
    SET 
      role = 'ADMIN',
      admin_level = 'ADMIN_4'
    WHERE id = NEW.id;
  END IF;

  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

-- Create a trigger on the profiles table that runs after insert
DROP TRIGGER IF EXISTS trigger_auto_promote_owner ON profiles;
CREATE TRIGGER trigger_auto_promote_owner
  AFTER INSERT ON profiles
  FOR EACH ROW
  EXECUTE FUNCTION auto_promote_owner();

-- Also create a manual function to promote existing users
CREATE OR REPLACE FUNCTION promote_user_to_admin_4(user_email TEXT)
RETURNS void AS $$
BEGIN
  UPDATE profiles 
  SET 
    role = 'ADMIN',
    admin_level = 'ADMIN_4'
  WHERE email = user_email;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

