-- =====================================================
-- 9. Notify on Sponsorship Deal Update
-- =====================================================
CREATE OR REPLACE FUNCTION notify_deal_update()
RETURNS TRIGGER AS $$
DECLARE
  sponsor_name TEXT;
  club_name TEXT;
BEGIN
  -- Get names
  SELECT name INTO sponsor_name FROM profiles WHERE id = NEW.sponsor_id;
  SELECT name INTO club_name FROM profiles WHERE id = NEW.club_id;

  -- Notify club on new deal request
  IF TG_OP = 'INSERT' THEN
    INSERT INTO notifications (user_id, type, content, related_id)
    VALUES (
      NEW.club_id,
      'deal_update',
      sponsor_name || ' hat ein Sponsorenpaket angefragt',
      NEW.id
    );
  -- Notify both parties on status change
  ELSIF TG_OP = 'UPDATE' AND OLD.status != NEW.status THEN
    -- Notify sponsor
    INSERT INTO notifications (user_id, type, content, related_id)
    VALUES (
      NEW.sponsor_id,
      'deal_update',
      'Deal-Status aktualisiert: ' || NEW.status,
      NEW.id
    );

    -- Notify club
    INSERT INTO notifications (user_id, type, content, related_id)
    VALUES (
      NEW.club_id,
      'deal_update',
      'Deal-Status aktualisiert: ' || NEW.status,
      NEW.id
    );
  END IF;

  RETURN NEW;
END;
$$ LANGUAGE plpgsql SECURITY DEFINER;

CREATE TRIGGER trigger_notify_deal_update
  AFTER INSERT OR UPDATE ON sponsorship_deals
  FOR EACH ROW
  EXECUTE FUNCTION notify_deal_update();
